import "./house.css";
import NumberFormat from "react-number-format";
import { useNavigate } from "react-router-dom";
import emailIcon from "./email.png";
import { useState } from "react";
import Inquiry from "./Inquiry";
const House = ({ house }: { house: any }) => {
  const [inquiryShow, setInquiryShow] = useState(false);
  const history = useNavigate();
  const handleInquiryClick = () => {
    setInquiryShow(!inquiryShow);
  };
  const handleBackClick = () => {
    history(`/SearchResults/${house.country}`);
  };
  return (
    <>
      <div>
        <div className="row mt-2">
          <h5 className="col-md-12">{house.country}</h5>
        </div>
        <div className="row">
          <h3 className="col-md-12">{house.address}</h3>
        </div>
        <div className="row">
          <img
            width="200px"
            style={{ width: 300 }}
            height="200px"
            src={process.env.PUBLIC_URL + "/images/" + house.photo + ".png"}
            alt="Home"
          />
        </div>
        <div className="col-md-5">
          <p className="price">
            <NumberFormat
              value={house.price}
              displayType={"text"}
              thousandSeparator={true}
              prefix={"£"}
            />
          </p>
          <p>{house.description}</p>
        </div>
        <img
          src={emailIcon}
          height="50"
          width="50"
          alt="E-Mail"
          onClick={handleInquiryClick}
        />
        {inquiryShow && <Inquiry house={house} />}
      </div>
      <button onClick={handleBackClick}>Back to Results</button>
    </>
  );
};
export default House;
