import { useEffect, useState, useMemo } from "react";
import "./main-page.css";
import Header from "./header";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import FeaturedHouse from "./featured-house";
import HouseFilter from "./filter-house";
import SearchResults from "../search-results/search-results";
//useEffect - create side effect when stat of component changes
//useMemo - cache'ing

const App= () => {
  const [allHouses, setAllHouses] = useState([]);

  useEffect(() => {
    const fetchHouses = async () => {
      const resp = await fetch("../../public/houses.json");
      const houses = await resp.json();
      setAllHouses(houses);
    };
    fetchHouses();
  }, []);

  const featureHouses = useMemo(() => {
    if (allHouses.length) {
      const randomIndex = Math.floor(Math.random() * allHouses.length);
      return allHouses[randomIndex];
    }
  }, [allHouses]);

  /*
                    <Router>
always rendered  ->    <h1>Hi there</h1>
                      <Switch>
www.me.com/about ->    <Route exact path="/about"> exact prevent partial match
                        <About></About>
                        </Route>
www.me.com ->           <Route path="/">
                          <FeaturedHouse></FeaturedHouse>
                        </Route>
                      </Switch>
                    </Router>
*/

  return (
    <Router>
      <div className="container">
        <Header subtitle="Proving houses all over the world" />
        <HouseFilter allHouses={allHouses} />
        <Routes>
          <Route path="/searchresults/:country">
            <SearchResults allHouses={allHouses}></SearchResults>
          </Route>
          <Route path="/">
            <FeaturedHouse house={featureHouses}></FeaturedHouse>
          </Route>
        </Routes>
      </div>
    </Router>
  );
}

export default App;
